import { Component } from '@angular/core';

import { Product, products } from '../products';

@Component({
  selector: 'app-product-list',
  templateUrl: './product-list.component.html',
  styleUrls: ['./product-list.component.css'],
})
export class ProductListComponent {
  products = [...products];

  share(product: Product): void {
    window.alert(`The product ${product.name} has been shared`);
  }

  onNotify(product: Product): void {
    window.alert(`You will be notified when ${product.name} goes on sale`);
  }
}
<h2>Products</h2>
<div *ngFor="let product of products">
  <h3>
    <a 
    [title]="product.name + ' details'"
    [routerLink]="['/products', product.id]">
      {{ product.name }}
    </a>
  </h3>

  <p *ngIf="product.description">Description: {{ product.description }}</p>

  <button type="button" (click)="share(product)">Share</button>

  <app-product-alerts 
    [product]="product" 
    (notify)="onNotify(product)">
  </app-product-alerts>
</div>
