import { Component } from '@angular/core';
import { FormBuilder } from '@angular/forms';

import { CartService } from '../cart.service';
import { Product } from '../products';

@Component({
  selector: 'app-cart',
  templateUrl: './cart.component.html',
  styleUrls: ['./cart.component.css']
})
export class CartComponent {

  items = this.cartService.getItems();

  //forma um grupo com os atributos name e adress
  checkoutForm = this.formBuilder.group({
    name: '',
    adress: ''
  })

  constructor(
    private cartService: CartService,
    private formBuilder: FormBuilder
    ) { }

  removeFromCart(product: Product): void {
    this.items = this.cartService.removeProduct(product);
  }

  //Limpa o carrinho, imprime que a compra foi um sucesso no console e limpa o checkoutForm
  onSubmit(): void {
    this.items = this.cartService.clearCart();

    console.warn('Your order has been submited', this.checkoutForm.value);
    this.checkoutForm.reset();
  }

}
<h1>My Cart</h1>

<p>
  <a routerLink="/shipping">Shipping Prices</a>
</p>

<div class="cart-item" *ngFor="let item of items">
  <span>{{ item.name }}</span>
  <span>{{ item.price | currency }}</span>
  <span>{{ item.count}}</span>
  <span class="material-icons" (click)="removeFromCart(item)">delete</span>
</div>

<!-- o formGroup vai ser o grupo criada no ts com os atributos name e adress
    ngSubmit chama a função onSubmit que será acionado ao clicar no botão com o tipo submit -->
<form [formGroup]="checkoutForm" (ngSubmit)="onSubmit()">

  <!-- O formControlName captura a partir do nome do atributo do grupo checkoutForm -->
  <div>
    <label for="name">
      Name
    </label>
    <input id="name" type="text" formControlName="name">
  </div>

  <div>
    <label for="adress">
      Adress
    </label>
    <input id="adress" type="text" formControlName="adress">
  </div>

  <button class="button" type="submit">Purchase</button>
</form>
